import request from "@/utils/request.js";
export default {
  name: "Register",
  data() {
    return {
      form: {},
      rules: {
        username: [{
          required: true,
          message: "请输入账号",
          trigger: "blur"
        }],
        password: [{
          required: true,
          message: "请输入密码",
          trigger: "blur"
        }, {
          min: 6,
          max: 10,
          message: "长度在 6 到 10 个字符",
          trigger: "blur"
        }],
        confirm: [{
          required: true,
          message: "请输入确认密码",
          trigger: "blur"
        }, {
          min: 6,
          max: 10,
          message: "长度在 6 到 10 个字符",
          trigger: "blur"
        }]
      }
    };
  },
  methods: {
    register() {
      if (this.form.password !== this.confirm.password) {
        this.$message({
          type: "error",
          message: "两次密码输入不一致"
        });
        return;
      }
      this.$refs['form'].validate(valid => {
        if (valid) {
          request.post("api/user/register", this.form).then(res => {
            if (res.code === "0") {
              this.$message({
                type: "success",
                message: "注册成功"
              });
              this.$router.push("/"); //注册成功后进行跳转，跳转到主页
            } else {
              this.$message({
                type: "error",
                message: res.msg
              });
            }
          });
        }
      });
    }
